import { useReducer } from "react";

import CartContext from "./cart-content";

const defauktCartState = {
  items: [],
  totalItems: 0
};

const cartReducer = (state, action) => {
  if (action.type === "ADD") {
    console.log("add", state)
    const updatedItems = state.items.concat(action.items);
    const updatedTotalAmount = state.totalAmaunt + action.item.price * action.item.amount;
    return {
      items: updatedItems,
      totalAmaunt: updatedTotalAmount
    };
  }
  return defauktCartState
};

const CardProvider = (props) => {

  const [cartState, dispatchCartAction] = useReducer(cartReducer, defauktCartState)

  const addItemCartHandler = (item) => {
    dispatchCartAction({type: "ADD", item: item})
  };

  const removeItemHandler = (id) => {
    dispatchCartAction({type: "REMOVE", id: id})
  };

  const cartContext = {
    items: cartState.items,
    totalAmaunt: cartState.totalAmaunt,
    addItem: addItemCartHandler,
    removeItem: removeItemHandler,
  };

  return (
    <CartContext.Provider value={cartContext}>
      {props.children}
    </CartContext.Provider>
  );
};

export default CardProvider;
